enum NumberingType {
  /// For lowercase letters
  lowercaseLetters('a'),

  /// For uppercase letters
  uppercaseLetters('A'),

  /// For lowercase Roman numerals
  lowercaseRomanNumerals('i'),

  /// For uppercase Roman numerals
  uppercaseRomanNumerals('I'),

  /// For numbers (default)
  numbers('1');

  const NumberingType(this.value);

  final String value;
}

/// {@template jaspr.html.li}
/// The &lt;li&gt; HTML element is used to represent an item in a list. It must be contained in a parent element: an ordered list (&lt;ol&gt;), an unordered list (&lt;ul&gt;), or a menu (&lt;menu&gt;). In menus and unordered lists, list items are usually displayed using bullet points. In ordered lists, they are usually displayed with an ascending counter on the left, such as a number or letter.
/// {@endtemplate}
class li extends StatelessComponent {
  /// {@macro jaspr.html.li}
  const li(
    this.children, {
    this.value,
    this.id,
    this.classes,
    this.styles,
    this.attributes,
    this.events,
    super.key,
  });

  /// This integer attribute indicates the current ordinal value of the list item as defined by the &lt;ol&gt; element. The only allowed value for this attribute is a number, even if the list is displayed with Roman numerals or letters. List items that follow this one continue numbering from the value set. The value attribute has no meaning for unordered lists (&lt;ul&gt;) or for menus (&lt;menu&gt;).
  final int? value;

  /// The id of the HTML element. Must be unique within the document.
  final String? id;

  /// The CSS classes to apply to the HTML element, separated by whitespace.
  final String? classes;

  /// The inline styles to apply to the HTML element.
  final Styles? styles;

  /// Additional attributes to apply to the HTML element.
  final Map<String, String>? attributes;

  /// Event listeners to attach to the HTML element.
  final Map<String, EventCallback>? events;

  /// The children of this component.
  final List<Component> children;

  @override
  Component build(BuildContext context) {
    return Component.element(
      tag: 'li',
      id: id,
      classes: classes,
      styles: styles,
      attributes: {...?attributes, 'value': ?value?.toString()},
      events: events,
      children: children,
    );
  }
}

